package com.testwa.distest.server.websocket.handler;import com.corundumstudio.socketio.AckRequest;import com.corundumstudio.socketio.SocketIOClient;import com.corundumstudio.socketio.annotation.OnEvent;import com.testwa.distest.server.service.apitest.service.ApiService;import com.testwa.distest.server.service.cache.mgr.SubscribeMessageMgr;import com.testwa.distest.server.web.apitest.mgr.ApiSendMgr;import com.testwa.distest.server.websocket.service.MessageDefine;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;/** * @Program: distest * @Description: 处理页面消息 * @Author: wen * @Create: 2018-04-28 10:13 **/@Slf4j@Componentpublic class ApiSenderHandler {    @Autowired    private ApiSendMgr apiSendMgr;    @OnEvent(value = "send")    public void onSendAction(SocketIOClient client, Long apiId, AckRequest ackRequest) {        if(apiId == null){            log.error("apiId is null!");            client.sendEvent("error", "apiId不能为空");            return;        }    }}