package com.testwa.distest.server.websocket.handler;import com.corundumstudio.socketio.AckRequest;import com.corundumstudio.socketio.SocketIOClient;import com.corundumstudio.socketio.annotation.OnEvent;import com.testwa.distest.server.service.cache.mgr.DeviceSessionMgr;import com.testwa.distest.server.websocket.service.PushCmdService;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;@Slf4j@Componentpublic class TaskHandler {    private final static String startTask = "start_task";    private final static String cancelTask = "cancel_task";    private final static String completeTask = "complete_task";    @Autowired    private DeviceSessionMgr deviceSessionMgr;    @Autowired    private PushCmdService pushCmdService;    @OnEvent(value = startTask)    public void onStartTask(SocketIOClient client, String data, AckRequest ackRequest) {        if("true".equals(data)){            log.info("task start success!");        }        if("false".equals(data)){            log.info("task start error!");        }    }    @OnEvent(value = cancelTask)    public void onCancelTask(SocketIOClient client, String data, AckRequest ackRequest) {        log.info("task cancel success!");    }    @OnEvent(value = completeTask)    public void onCompleteTask(SocketIOClient client, String data, AckRequest ackRequest) {        log.info("task complete success!");    }}