mapper.update:
update device
onlineStatus=#{onlineStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}, workStatus=#{workStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}, debugStatus=#{debugStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}, updateTime=#{updateTime}, model=#{model}, brand=#{brand}, cpuabi=#{cpuabi}, sdk=#{sdk}, width=#{width}, height=#{height}, osName=#{osName}, density=#{density}, osVersion=#{osVersion}, host=#{host}, lastUserId=#{lastUserId}, lastUserToken=#{lastUserToken}, stfagentInstall=#{stfagentInstall}, appiumserverInstall=#{appiumserverInstall}, appiumserverdebugInstall=#{appiumserverdebugInstall}, keyboardserviceInstall=#{keyboardserviceInstall}, settingsInstall=#{settingsInstall}, unlockInstall=#{unlockInstall}, unicodeIMEInstall=#{unicodeIMEInstall}, selendroidInstall=#{selendroidInstall}
WHERE deviceId = #{deviceId}

mapper.select:
SELECT id, deviceId, phoneOS, updateTime, model, brand, cpuabi, sdk, width, height, osName, density, host, lastUserId,
1 AS onlineStatus, workStatus, debugStatus, osVersion,stfagentInstall,appiumserverInstall,appiumserverdebugInstall,keyboardserviceInstall,
settingsInstall,unlockInstall,unicodeIMEInstall,selendroidInstall
FROM device

    and deviceId = #{deviceId}

        and deviceId in #{devId} 

    and phoneOS = #{phoneOS}
    and onlineStatus = #{onlineStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}
    and workStatus = #{workStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}
    and debugStatus = #{debugStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}
    and model = #{model}
    and brand = #{brand}
    and lastUserId = #{lastUserId}
    and width = #{width}
    and height = #{height}
    and osVersion = #{osVersion}

SELECT id, deviceId, phoneOS, updateTime, model, brand, cpuabi, sdk, width, height, osName, density, host, lastUserId,
1 AS onlineStatus, workStatus, debugStatus, osVersion,stfagentInstall,appiumserverInstall,appiumserverdebugInstall,keyboardserviceInstall,
settingsInstall,unlockInstall,unicodeIMEInstall,selendroidInstall
FROM device

    and deviceId = #{deviceId}

        and deviceId in #{devId} 

    and phoneOS = #{phoneOS}
    and onlineStatus = #{onlineStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}
    and workStatus = #{workStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}
    and debugStatus = #{debugStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}

        and
        (model LIKE CONCAT('%', #{brand}, '%') or brand LIKE CONCAT('%', #{brand}, '%'))

    and lastUserId = #{lastUserId}
    and width = #{width}
    and height = #{height}
    and osVersion = #{osVersion}

SELECT id, deviceId, phoneOS, updateTime, model, brand, cpuabi, sdk, width, height, osName, density, host, lastUserId, osVersion, workStatus,

  CASE WHEN deviceId in #{onlineDev}  THEN 1 ELSE 2 END AS onlineStatus


    2 as onlineStatus

,stfagentInstall,appiumserverInstall,appiumserverdebugInstall,keyboardserviceInstall,settingsInstall,unlockInstall,unicodeIMEInstall,selendroidInstall
FROM device

    and deviceId = #{query.deviceId}
    and phoneOS = #{query.phoneOS}
    and onlineStatus = #{query.onlineStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}
    and model = #{query.model}
    and brand = #{query.brand}
    and lastUserId = #{query.lastUserId}

SELECT device.id, device.deviceId, device.phoneOS, device.updateTime, device.model, device.brand, device.cpuabi, device.sdk,
device.width, device.height, device.osName, device.osVersion, device.density, device.host,device.stfagentInstall,
device.appiumserverInstall,device.appiumserverdebugInstall,device.keyboardserviceInstall,device_auth.id as deviceAuth_id,
device.settingsInstall,device.unlockInstall,device.unicodeIMEInstall,device.selendroidInstall,
device_auth.userId,user.id as user_id,user.username as username, user.email as email,

    CASE WHEN device.deviceId in #{onlineDev}  THEN 1 ELSE 2 END AS onlineStatus


    2 as onlineStatus

FROM device LEFT JOIN device_auth ON device.deviceId = device_auth.deviceId and device.lastUserId = device_auth.createBy LEFT JOIN user ON device_auth.userId = user.id

    and device.deviceId = #{deviceId}

        and device.deviceId in #{devId} 

    and device.phoneOS = #{phoneOS}
    and device.onlineStatus = #{onlineStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}
    and device.model = #{model}
    and device.brand = #{brand}
    and device.lastUserId = #{createBy}

SELECT count(1) c FROM device

    and lastUserId = #{lastUserId}
    and device.onlineStatus = #{onlineStatus, typeHandler=com.testwa.core.base.mybatis.EnumValueHandler}

select concat(width, 'x', height) as name, count(1) value from device
width != '' and height != '' and deviceId in #{devId}
group by concat(width, 'x', height)

select osVersion as name, count(1) value from device
osVersion is not null and deviceId in #{devId}
group by osVersion

select brand as name, count(1) value from device
brand is not null and deviceId in #{devId}
group by brand
