mapper.select:
SELECT distinct t.*
FROM dis_issue t
left join dis_issue_label_map m on t.id = m.issue_id
left join dis_issue_assignee a on t.id = a.issue_id
WHERE t.enabled = 1
and t.project_id = #{query.projectId}
and t.author_id = #{query.authorId}
and t.state = #{query.state}
and t.title like CONCAT('%', #{query.search}, '%')
and a.assignee_id = #{query.assigneeId}

  and m.label_id in #{item}

SELECT t.state as issueState, count(distinct t.id) as countValue
FROM dis_issue t
left join dis_issue_label_map m on t.id = m.issue_id
left join dis_issue_assignee a on t.id = a.issue_id
WHERE t.enabled = 1
and t.project_id = #{query.projectId}
and t.author_id = #{query.authorId}
and t.state = #{query.state}
and t.title like CONCAT('%', #{query.search}, '%')
and a.assignee_id = #{query.assigneeId}

  and m.label_id in #{item} 

group by t.state

mapper.update:
update dis_issue
set comment_num = comment_num + 1
where id = #{issueId}
